from bs4 import BeautifulSoup
import base64
import hashlib

html_data = '<p>&nbsp;</p><figure class="image"><img style="aspect-ratio:28/28;" src="data:image/png;base64," width="28" height="28"></figure><p>&nbsp;</p><figure class="image"><img style="aspect-ratio:720/850;" src="https://i.namu.wiki/i/VVGxk8g9vg8gX1M-J2XXv-cZoOh03iwUC7rsTj7duyT4w83AyBWwbHoOe_3-Rixx01NvE6lNM9_LnvuwetgI97Se0JyVV4vgofT_fZJEydajEOFLP34gkql0pvRbNu3ciG9WfyhiJkv1WTiB_VdwtA.webp" alt="2025미지" width="720" height="850"></figure>'


soup = BeautifulSoup(html_data, "lxml")

urls = []
figures = soup.find_all("figure", class_="image")
for figure in figures:
    img = figure.find("img")
    src = img["src"]
    if src.startswith("data:image"):
        format, img_data = src.split(";base64,")

        image_type = format.split("/")[-1]

        image_data = base64.b64decode(img_data)
        file_name = f"{hashlib.md5(image_data).hexdigest()}.{image_type}"
        file_path = f"./src/storage/{file_name}"
        with open(file_path, "wb") as f:
            f.write(image_data)
        urls.append(file_path)

idx = 0
for figure in figures:
    img = figure.find("img")
    if img["src"].startswith("data:image"):
        img["src"] = urls[idx]
        idx += 1

print(soup.prettify())


